import React, { Component } from 'react';

class Items extends Component {
    state = {
        numOfFollowers: [],
        numOfRepos: [],
    }

    componentDidMount(props){
        console.log("hi items")
        let numOfFollowers = [];
        let numOfRepos = [];
        if (this.props.kind === "usersFollowers") {
            this.props.items.map( item => {
                fetch(`https://api.github.com/users/${item.login}`)
                    .then(response => response.json())
                    .then(data => {
                        numOfFollowers.push(data.followers)
                        numOfRepos.push(data.public_repos)
                    })
            })
            this.setState({numOfFollowers, numOfRepos})
        }
    }

    render(props) { 

        if (this.props.battle) return <div>BATTLE!</div>

        if (!this.props.items) return null

        if (this.props.kind === "usersFollowers") {
            return (
                this.props.items.map( (item, index) => 
                    <div 
                        className="col-md-3 mb-3 d-flex justify-content-center"
                        key={item.id}    
                    >
                        <div className="card" style={{width: "20rem"}}>
                            <img src={item.avatar_url} className="card-img-top" alt={item.login} />
                            <div className="card-body">
                                <h5 className="card-title">{item.login}</h5>
                                <p className="card-text">Followers: {this.state.numOfFollowers[index]}</p>
                                <p className="card-text">Repositories: {this.state.numOfRepos[index]}</p>
                                <a href={item.html_url} className="btn btn-primary">Account</a>
                            </div>
                        </div>
                    </div>
                )
            )

        }

        if (this.props.kind === "usersRepos") {
            return ( 
                this.props.items.map( item => 
                    <div 
                        className="col-md-4 mb-3"
                        key={item.id}    
                    >
                        <div className="card" style={{width: "18rem"}}>
                            <img src="..." className="card-img-top" alt="..." />
                            <div className="card-body">
                                <h5 className="card-title">Card title</h5>
                                <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                <a href="#" className="btn btn-primary">Go somewhere</a>
                            </div>
                        </div>
                    </div>
                )
            )
        }

    }
}

export default Items;
