import React, { Component } from 'react';
import './App.css';
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items"

class App extends Component {

  state = { 
    usersFollowers: [],
    usersRepos: [],
    reposJavascript: [],
    reposJava: [],
    reposPython:[],
    reposPhp: []
  };

  componentDidMount(){
   console.log('App - Mounted')
  //  fetch("https://api.github.com/search/users?q=tom")

    // fetch("https://api.github.com/search/users?q=followers%3A%3E15000")
    fetch("https://api.github.com/search/users??utf8=%E2%9C%93&q=followers%3A%3E15000")
      .then(response => response.json())
      .then(data => {
        console.log(data);
        const usersFollowers = data.items;
        this.setState({usersFollowers}) 
      })

    fetch("https://api.github.com/search/users?utf8=%E2%9C%93&q=repos%3A%3E9400")
      .then(response => response.json())
      .then(data => {
        console.log(data);
        const usersRepos = data.items;
        this.setState({usersRepos}) 
      })



    // fetch("https://api.github.com/search/users?q=ahmadghzawi")
    //   .then(response => response.json())
    //   .then(data => console.log(data))
  };

  render() {
    console.log('App - Rendered');
    return (
      <React.Fragment>
        <Header />
        <main className="container mt-5">
          <div className="row">
            <Items 
              followers={this.state.usersFollowers}
              repos={this.state.usersRepos}
              js={this.state.reposJavascript}
              java={this.state.reposJava}
              python={this.state.reposPython}
              php={this.state.reposPhp}
            />
          </div>
        </main>
        <Footer />
      </React.Fragment>
    );
  };

}

export default App;
