import React, { Component } from 'react';
import './App.css';
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items"

class App extends Component {

  state = { 
    usersFollowers: [],
    usersFollowersNumOfFollowers: [],
    usersFollowersNumOfRepos: [],

    usersRepos: [],
    usersReposNumOfFollowers: [],
    usersReposNumOfRepos: [],

    reposJavascript: [],
    reposJavascriptNumOfFollowers: [],
    reposJavascriptNumOfRepos: [],

    reposJava: [],
    reposJavaNumOfFollowers: [],
    reposJavaNumOfRepos: [],

    reposPython:[],
    reposPythonNumOfFollowers: [],
    reposPythonNumOfRepos: [],

    reposPhp: [],
    reposPhpNumOfFollowers: [],
    reposPhpNumOfRepos: [],

    itemsToShow: null,
    kind: null,
    battle: false
  };

  componentWillMount(){
    console.log('App - Mounted')

    let usersFollowers, usersRepos, reposJavascript, reposJava, reposPython, reposPhp;
    let usersFollowersNumOfFollowers = []
    let usersFollowersNumOfRepos = []

    fetch("https://api.github.com/search/users?utf8=%E2%9C%93&q=followers%3A%3E15000")
    .then(response => response.json())
    .then(data => {
      usersFollowers = data.items;
    })

    fetch("https://api.github.com/search/users?utf8=%E2%9C%93&q=repos%3A%3E9400")
    .then(response => response.json())
    .then(data => {
      console.log(data);
      usersRepos = data.items;
    })

    fetch("https://api.github.com/search/repositories?utf8=%E2%9C%93&q=stars%3A%22%3E+41000%22+language%3AJavaScript")
    .then(response => response.json())
    .then(data => {
      reposJavascript = data.items;
    })

    fetch("https://api.github.com/search/repositories?utf8=%E2%9C%93&q=stars%3A%22%3E+18140%22+language%3AJava")
    .then(response => response.json())
    .then(data => {
      reposJava = data.items;
    })

    fetch("https://api.github.com/search/repositories?utf8=%E2%9C%93&q=stars%3A%22%3E+21500%22+language%3APython")
    .then(response => response.json())
    .then(data => {
      reposPython = data.items;
    })

    fetch("https://api.github.com/search/repositories?utf8=%E2%9C%93&q=stars%3A%22%3E+9500%22+language%3APHP")
    .then(response => response.json())
    .then(data => {
      reposPhp = data.items;
    })

    setTimeout(() => {
      usersFollowers.map( item => {
        fetch(`https://api.github.com/users/${item.login}`)
            .then(response => response.json())
            .then(data => {
              console.log(data.followers);
              usersFollowersNumOfFollowers.push(data.followers)
              usersFollowersNumOfRepos.push(data.public_repos)
            })
      })

      this.setState({usersFollowers, usersFollowersNumOfFollowers, usersFollowersNumOfRepos, usersRepos, reposJavascript, reposJava, reposPython, reposPhp, itemsToShow: usersFollowers, kind: "usersFollowers"})
    }, 2500)

  };

  handleClick = (chosen) => {
    chosen === "battle" ? 
      this.setState({itemsToShow: null, kind: null, battle: true}) : 
      this.setState({itemsToShow: this.state[chosen], kind: chosen, battle: false})
  }

  render() {
    console.log('App - Rendered');
    console.log(this.state);
    return (
      <React.Fragment>
        <Header handleClick={this.handleClick} />
        <hr />
        <main className="container-fluid mt-5">
          <div className="row">
            <Items
              wait={5000}
              state={this.state}
            />
          </div>
        </main>
        <Footer 
          battle={this.state.battle}
          items={this.state.itemsToShow}  
        />
      </React.Fragment>
    );
  };

}

export default App;
