import React, { Component } from 'react';
import './App.css';
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items"

class App extends Component {

  state = { 
    usersFollowers: [],
    usersRepos: [],
    reposJavascript: [],
    reposJava: [],
    reposPython:[],
    reposPhp: []
  };

  componentDidMount(){
    console.log('App - Mounted')

    let usersFollowers;
    let usersRepos;
    let reposJavascript;
    let reposJava;
    let reposPython;
    let reposPhp;

    const waitFetching = () => {
      setTimeout(() => {
        fetch("https://api.github.com/search/users??utf8=%E2%9C%93&q=followers%3A%3E15000")
        .then(response => response.json())
        .then(data => {
          usersFollowers = data.items;
          console.log('usersFollowers:', usersFollowers)
        })

        fetch("https://api.github.com/search/users?utf8=%E2%9C%93&q=repos%3A%3E9400")
        .then(response => response.json())
        .then(data => {
          usersRepos = data.items;
          console.log('usersRepos:', usersRepos)
        })

        fetch("https://api.github.com/search/repositories?utf8=%E2%9C%93&q=stars%3A%22%3E+41000%22+language%3AJavaScript")
        .then(response => response.json())
        .then(data => {
          reposJavascript = data.items;
          console.log('reposJavascript:', reposJavascript)
        })

        fetch("https://api.github.com/search/repositories?utf8=%E2%9C%93&q=stars%3A%22%3E+18140%22+language%3AJava")
        .then(response => response.json())
        .then(data => {
          reposJava = data.items;
          console.log('reposJava:', reposJava)
        })

        fetch("https://api.github.com/search/repositories?utf8=%E2%9C%93&q=stars%3A%22%3E+21500%22+language%3APython")
        .then(response => response.json())
        .then(data => {
          reposPython = data.items;
          console.log('reposPython:', reposPython)
        })

        fetch("https://api.github.com/search/repositories?utf8=%E2%9C%93&q=stars%3A%22%3E+9500%22+language%3APHP")
        .then(response => response.json())
        .then(data => {
          reposPython = data.items;
          console.log('reposPython:', reposPython)
        })
      }, 5000)
    }

    waitFetching();
    this.setState({usersFollowers, usersRepos, reposJavascript, reposJava, reposPython, reposPhp})
    console.log(usersFollowers);

  };

  render() {
    console.log('App - Rendered');
    return (
      <React.Fragment>
        <Header />
        <main className="container mt-5">
          <div className="row">
            <Items 
              followers={this.state.usersFollowers}
              repos={this.state.usersRepos}
              js={this.state.reposJavascript}
              java={this.state.reposJava}
              python={this.state.reposPython}
              php={this.state.reposPhp}
            />
          </div>
        </main>
        <Footer />
      </React.Fragment>
    );
  };

}

export default App;
