import React, { Component } from 'react';

class Items extends Component {

    render(props) { 

        if (this.props.state.battle) return <div>BATTLE!</div>

        if (!this.props.state.itemsToShow) return null

        if (this.props.state.kind === "usersFollowers") {
            console.log("cards");
            NumOfFollowers=this.props
            return (
                this.props.state.itemsToShow.map( (item, index) => 
                    <div 
                        className="col-md-3 mb-3 d-flex justify-content-center"
                        key={item.id}    
                    >
                        <div className="card" style={{width: "20rem"}}>
                            <img src={item.avatar_url} className="card-img-top" alt={item.login} />
                            <div className="card-body">
                                <h5 className="card-title">{item.login}</h5>
                                <p className="card-text">Followers: {numOfFollowers[index]}</p>
                                <p className="card-text">Repositories: {this.props.state.usersReposNumOfRepos[index]}</p>
                                <a href={item.html_url} className="btn btn-primary">Account</a>
                            </div>
                        </div>
                    </div>
                )
            )

        }

        if (this.props.kind === "usersRepos") {
            return ( 
                this.props.items.map( item => 
                    <div 
                        className="col-md-4 mb-3"
                        key={item.id}    
                    >
                        <div className="card" style={{width: "18rem"}}>
                            <img src="..." className="card-img-top" alt="..." />
                            <div className="card-body">
                                <h5 className="card-title">Card title</h5>
                                <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                <a href="#" className="btn btn-primary">Go somewhere</a>
                            </div>
                        </div>
                    </div>
                )
            )
        }

    }
}

export default Items;
