import React, { Component } from 'react';
import './App.css';
import Header from "./components/Header";
import Footer from "./components/Footer";
import Items from "./components/Items"

class App extends Component {

  state = { 
    usersFollowers: [],
    usersFollowersNumOfFollowers: [],
    usersFollowersNumOfRepos: [],

    usersRepos: [],
    usersReposNumOfFollowers: [],
    usersReposNumOfRepos: [],

    reposJavascript: [],
    reposJavascriptNumOfFollowers: [],
    reposJavascriptNumOfRepos: [],

    reposJava: [],
    reposJavaNumOfFollowers: [],
    reposJavaNumOfRepos: [],

    reposPython:[],
    reposPythonNumOfFollowers: [],
    reposPythonNumOfRepos: [],

    reposPhp: [],
    reposPhpNumOfFollowers: [],
    reposPhpNumOfRepos: [],

    itemsToShow: null,
    kind: null,
    battle: false,

    getAlso: false
  };

  async componentWillMount(){
    console.log('App - Will Mount')

    let usersFollowers = []
    let usersRepos = []
    let reposJavascript = []
    let reposJava = []
    let reposPython = []
    let reposPhp = []

    let response = await fetch("https://api.github.com/search/users?utf8=%E2%9C%93&q=followers%3A%3E15000")
    let data = await response.json()
    usersFollowers = data.items

    response = await fetch("https://api.github.com/search/users?utf8=%E2%9C%93&q=repos%3A%3E9400")
    data = await response.json()
    usersRepos = data.items

    response = await fetch("https://api.github.com/search/repositories?utf8=%E2%9C%93&q=stars%3A%22%3E+41000%22+language%3AJavaScript")
    data = await response.json()
    reposJavascript = data.items

    response = await fetch("https://api.github.com/search/repositories?utf8=%E2%9C%93&q=stars%3A%22%3E+18140%22+language%3AJava")
    data = await response.json()
    reposJava = data.items

    response = await fetch("https://api.github.com/search/repositories?utf8=%E2%9C%93&q=stars%3A%22%3E+21500%22+language%3APython")
    data = await response.json()
    reposPython = data.items

    response = await fetch("https://api.github.com/search/repositories?utf8=%E2%9C%93&q=stars%3A%22%3E+9500%22+language%3APHP")
    data = await response.json()
    reposPhp = data.items

      this.setState({usersFollowers, 
                    usersRepos, 
                    reposJavascript,
                    reposJava,
                    reposPython,
                    reposPhp,getAlso: true})
  };

  async componentDidMount(){
    console.log('App - Mounted')

    let usersFollowers = this.state.usersFollowers
    let usersFollowersNumOfFollowers = []
    let usersFollowersNumOfRepos = []

    let usersRepos = this.state.usersRepos
    let usersReposNumOfFollowers = []
    let usersReposNumOfRepos = []

    let reposJavascript = this.state.reposJavascript
    let reposJavascriptNumOfFollowers = []
    let reposJavascriptNumOfRepos = []

    let reposJava = this.state.reposJava
    let reposJavaNumOfFollowers = []
    let reposJavaNumOfRepos = []

    let reposPython = this.state.reposPython
    let reposPythonNumOfFollowers = []
    let reposPythonNumOfRepos = []

    let reposPhp = this.state.reposPhp
    let reposPhpNumOfFollowers = []
    let reposPhpNumOfRepos = []

    usersFollowers.map( item => {
      fetch(`https://api.github.com/users/${item.login}`)
          .then(response => response.json())
          .then(data => {
            usersFollowersNumOfFollowers.push(data.followers)
            usersFollowersNumOfRepos.push(data.public_repos)
          })
    })

    usersRepos.map( item => {
      fetch(`https://api.github.com/users/${item.login}`)
          .then(response => response.json())
          .then(data => {
            usersReposNumOfFollowers.push(data.followers)
            usersReposNumOfRepos.push(data.public_repos)
          })
    })

    reposJavascript.map( item => {
      fetch(`https://api.github.com/users/${item.owner.login}`)
          .then(response => response.json())
          .then(data => {
            reposJavascriptNumOfFollowers.push(data.followers)
            reposJavascriptNumOfRepos.push(data.public_repos)
          })
    })

    reposJava.map( item => {
      fetch(`https://api.github.com/users/${item.owner.login}`)
          .then(response => response.json())
          .then(data => {
            reposJavaNumOfFollowers.push(data.followers)
            reposJavaNumOfRepos.push(data.public_repos)
          })
    })

    reposPython.map( item => {
      fetch(`https://api.github.com/users/${item.owner.login}`)
          .then(response => response.json())
          .then(data => {
            reposPythonNumOfFollowers.push(data.followers)
            reposPythonNumOfRepos.push(data.public_repos)
          })
    })

    reposPhp.map( item => {
      fetch(`https://api.github.com/users/${item.owner.login}`)
          .then(response => response.json())
          .then(data => {
            reposPhpNumOfFollowers.push(data.followers)
            reposPhpNumOfRepos.push(data.public_repos)
          })
    })

    setTimeout(() => {
      this.setState({usersFollowersNumOfFollowers, usersFollowersNumOfRepos, 
                    usersReposNumOfFollowers, usersReposNumOfRepos, 
                    reposJavascriptNumOfFollowers, reposJavascriptNumOfRepos,
                    reposJavaNumOfFollowers, reposJavaNumOfRepos,
                    reposPythonNumOfFollowers, reposPythonNumOfRepos,
                    reposPhpNumOfFollowers, reposPhpNumOfRepos })
      console.log(this.state);
    }, 2500)
  }

  handleClick = (chosen) => {
    chosen === "battle" ? 
      this.setState({itemsToShow: null, kind: null, battle: true}) : 
      this.setState({itemsToShow: this.state[chosen], kind: chosen, battle: false})
  }

  render() {
    if (this.state.usersFollowers.length > 0){
    console.log('App - Rendered');
    console.log(this.state);
    return (
      <React.Fragment>
        <Header handleClick={this.handleClick} />
        <hr />
        <main className="container-fluid mt-5">
          <div className="row">
            <Items state={this.state} />
          </div>
        </main>
        <Footer 
          battle={this.state.battle}
          items={this.state.itemsToShow}  
        />
      </React.Fragment>
    );
  } else {
    return null
  }
  };

}

export default App;
